package com.example.unit_3_sprint_4_evaluation

import android.content.Context
import androidx.lifecycle.LiveData
import androidx.room.Room
import androidx.test.core.app.ApplicationProvider
import androidx.test.runner.AndroidJUnit4
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.launch
import org.junit.Before
import org.junit.Test
import org.junit.runner.RunWith
import org.robolectric.annotation.Config

@RunWith(AndroidJUnit4::class)
@Config(manifest = Config.NONE)
class TestingDatabase {
    lateinit var myDatabase: MyDatabase
    lateinit var myDao: MyDao
    @Before
    fun setUp(){
        val context: Context = ApplicationProvider.getApplicationContext<Context>()
        myDatabase= Room.inMemoryDatabaseBuilder(context,MyDatabase::class.java).build()
        myDao=myDatabase.getMyDao()
    }
    @Test
    fun  insertMyMoneyTest(){
        val myNewsEntity =MyNewsEntity("Liquor","Liquor should be ban","data:image/jpeg;base64,")
        CoroutineScope(Dispatchers.IO).launch {
            myDao.addTask(myNewsEntity)
            val allMoney : LiveData<List<MyNewsEntity>> =myDao.getTask()
            assert(allMoney.value?.isNotEmpty()!!)
        }
    }

}